import javax.swing.*;import java.awt.*;import java.util.ArrayList;import java.util.Random;public class Main extends JFrame {    static ArrayList<Point> points = new ArrayList<>();    static Quadtree qt = new Quadtree(new Rectangle(0, 0, 1000, 1000));    public Main() {        super("Quadtree");        getContentPane().setBackground(Color.WHITE);        setSize(1000, 1000);        setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);    }    public static void main(String[] args) {        Random r = new Random();        for (int i = 0; i < 500; i++) {            points.add(new Point(r.nextFloat() * (999), r.nextFloat() * (999), r.nextFloat() * (600)));        }        for (Point p : points) {            qt.insert(p);        }        SwingUtilities.invokeLater(() -> new Main().setVisible(true));    }    void drawPoints(Graphics g) {        Graphics2D g2d = (Graphics2D) g;        for (Point p : points) {            g2d.drawRect((int) p.getX() + 1, (int) p.getY() + 1, 2, 2);        }    }    public void paint(Graphics g) {        super.paint(g);        drawPoints(g);        qt.drawRectangles(g, qt.rect.getX(), qt.rect.getY(), qt.rect.getW(), qt.rect.getH());    }}