public class Point {    private float x;    private float y;    private float mass;    public Point(float x, float y, float mass) {        this.x = x;        this.y = y;        this.mass = mass;    }    public float getX() {        return x;    }    public void setX(float x) {        this.x = x;    }    public float getY() {        return y;    }    public void setY(float y) {        this.y = y;    }    public Point add(Point other) {        return new Point((this.x + other.x) / 2, (this.y + other.y) / 2, this.mass + other.mass);    }}