public class Rectangle {    private float w, h, x, y;    public Rectangle(float x, float y, float w, float h) {        this.w = w;        this.h = h;        this.x = x;        this.y = y;    }    public boolean contains(Point p) {        return (p.getX() >= x && p.getX() <= x + w                && p.getY() >= y && p.getY() <= y + h);    }    public float getW() {        return w;    }    public void setW(float w) {        this.w = w;    }    public float getH() {        return h;    }    public void setH(float h) {        this.h = h;    }    public float getX() {        return x;    }    public void setX(float x) {        this.x = x;    }    public float getY() {        return y;    }    public void setY(float y) {        this.y = y;    }}